import subprocess

from flask import Flask

app = Flask(__name__)


@app.route("/set/<option>")
def hello_world(option):
    match option:
        case "on":
            send_code("")

        case "off":
            send_code("")

        case "100":
            send_code("")

        case "75":
            send_code("")

        case "50":
            send_code("")

        case "25":
            send_code("")

    return f"{option}"


def send_code(code):
    subprocess.run(["sudo", "sendook", "-0", "100", "-1", "100", "-p", "3700", "-r", "10", code], check=True, text=True)
